package com.ame.ser.controller;import com.ame.ser.service.ex.ParameterInvalidException;import com.ame.ser.model.Ward;import com.ame.ser.service.WardService;import com.ame.ser.vo.ResultVO;import lombok.extern.slf4j.Slf4j;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.data.domain.Page;import org.springframework.web.bind.annotation.PathVariable;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;import java.util.List;/** * 病区设置控制器 * @author SWJ */@Slf4j@RestController@RequestMapping("ward")public class WardController extends BaseController {    @Autowired    private WardService wardService;    /**     * 创建病区     * @param ward     * @return     */    @RequestMapping("create")    public ResultVO<Void> createWard(@RequestBody Ward ward){        if (ward.getName()==null || ward.getName().isEmpty()) {            log.error("创建病区时，传入的名字为空");            throw new ParameterInvalidException("传入的参数是非法的，参考为:病区名字是空的");        }        wardService.createWard(ward);        return new ResultVO<>(SUCCESS);    }    /**     * 分页查询     * @param pageNo     * @param pageSize     * @return     */    @RequestMapping("show")    public ResultVO<Page<Ward>> showAllWardOnPage(int pageNo, int pageSize){        Page<Ward> PageWard =  wardService.findAllPage(pageNo, pageSize);        return new ResultVO<>(SUCCESS,PageWard);    }    /**     * 显示全部     * @return     */    @RequestMapping("showAll")    public ResultVO<List<Ward>> showAllWard(){        List<Ward> PageWard =  wardService.findAll();        return new ResultVO<>(SUCCESS,PageWard);    }    /**     * 通过id单个删除（或被迭代）     * @param id     * @return     */    @RequestMapping("/del/{id}")    public ResultVO<Void> del(@PathVariable("id") String id ){        wardService.delWardById(id);        return new ResultVO<>(SUCCESS);    }    /**     * 通过id多选删除     * @param ids  有，号的数据     * @return     */    @RequestMapping("delAll")    public ResultVO<Void> deleteByWardIdIn(  String ids){        List<String> LString = regDelAllData(ids);        wardService.deleteByWardIdIn(LString);        return new ResultVO<>(SUCCESS);    }    /**     * 修改数据     * @param ward     * @return     */    @RequestMapping("update")    public ResultVO<Void> update(@RequestBody Ward ward){        if (ward.getName()==null) {            log.error("修改病区时，传入的名字为空");            throw new ParameterInvalidException("传入的参数有误");        }        wardService.update(ward.getWardId() , ward.getName(), ward.getWardDescribe());        return new ResultVO<>(SUCCESS);    }}