package com.ame.ser.enums;import lombok.Getter;/** * 事件状态枚举 * @author SWJ */@Getterpublic enum EventStatusEnum {    STATUSENUM_0(0, "进行中"),    STATUSENUM_1(1, "待处理"),    STATUSENUM_2(2, "待结案"),    STATUSENUM_3(3, "已驳回"),    STATUSENUM_4(4, "已结案");    private final Integer code;    private final String message;    EventStatusEnum(Integer code, String message){        this.code = code;        this.message = message;    }}