package com.ame.ser.model;import lombok.Data;import javax.persistence.*;import java.util.Date;/** * 事件原因节点树 * @author SWJ */@Entity@Table(name = "eventReasonTree")@Datapublic class EventReasonTree {    /**     * 事件原因节点ID     */    @Id    @Column(length = 32)    private  String eventReasonTreeId;    /**     * 事件原因节点名称名称     */    @Column(length = 32)    private String name;    /**     * 事件原因节点名称名称     */    @Column(length = 32)    private String parentId;    /**     * 类型描述描述     */    @Column(length = 100)    private String eventReasonTreeDescribe;    @Temporal(TemporalType.TIMESTAMP)    private Date createTime;    @Temporal(TemporalType.TIMESTAMP)    private Date updateTime;}