package com.ame.ser.repository;import com.ame.ser.model.EventLevel;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.data.jpa.repository.Modifying;import org.springframework.data.jpa.repository.Query;import org.springframework.data.repository.query.Param;import org.springframework.transaction.annotation.Transactional;import java.util.List;/** * 事件等级持久层 * @author SWJ */public interface EventLevelRepository extends JpaRepository<EventLevel,String> {    /**     * 通过Name字段查找     * @param name     * @return     */    EventLevel getByName(String name);    /**     * 多选删除     * @param ids     * @return     */    @Modifying    @Transactional    @Query(value="delete from EventLevel e where e.id in (:ids) ")    int deleteByEventLevelIdIn(@Param("ids")List<String> ids);}