package com.ame.ser.repository;import com.ame.ser.model.EventReasonTree;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.data.jpa.repository.Modifying;import org.springframework.data.jpa.repository.Query;import org.springframework.data.repository.query.Param;import org.springframework.transaction.annotation.Transactional;import java.util.List;/** * 事件原因树持久层 * @author SWJ */public interface EventReasonTreeRepository extends JpaRepository<EventReasonTree,String> {    /**     * 通过Name字段查找     * @param name     * @return     */    EventReasonTree getByName(String name);    /**     * 多选删除     * @param ids     * @return     */    @Modifying    @Query(value="delete from EventReasonTree e where e.id in (:ids) ")    int deleteByEventReasonTreeIdIn(@Param("ids")List<String> ids);}