package com.ame.ser.repository;import com.ame.ser.model.EventType;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.data.jpa.repository.Modifying;import org.springframework.data.jpa.repository.Query;import org.springframework.data.repository.query.Param;import java.util.List;import java.util.Optional;/** * 事件类型持久层 * * @author SWJ */public interface EventTypeRepository extends JpaRepository<EventType, String> {    /**     * 通过Name字段查找     * @param name     * @return     */    EventType getByName(String name);    /**     * 多选删除     * @param ids     * @return     */    @Modifying    @Query(value = "delete from EventType e where e.id in (:ids) ")    int deleteByEventTypeIdIn(@Param("ids") List<String> ids);    List<EventType> findByParentId(String id);    Optional<EventType> findById(String id);}