package com.ame.ser.service;import com.ame.ser.enums.DictTypeEnum;import com.ame.ser.model.Dict;import com.ame.ser.service.ex.TableFieldContentDuplicateException;import org.springframework.data.domain.Page;import java.util.List;/** * 字典库业务层 * @author SWJ */public interface DictService {    /**     * 创建字典库     * @param dict     * @throws TableFieldContentDuplicateException     */    void createDict(Dict dict) throws TableFieldContentDuplicateException;    /**     * 分页查询     * @param pageNo     * @param pageSize     * @return     */    Page<Dict> findAllPage(int pageNo, int pageSize);    /**     * 查询全部     * @return     */    List<Dict> findAll();    /**     * 单个删除（或被迭代）     * @param dictId     */    void delDictById(String dictId);    /**     * 多选删除     * @param ids     */    void deleteByDictIdIn(List<String > ids);    /**     * 修改数据     * @param dictId     * @param name     * @param dictDescribe     * @param dictType     */    void update(String dictId,String name,String dictDescribe,DictTypeEnum dictType);}