package com.ame.ser.service;import com.ame.ser.model.EventLevel;import com.ame.ser.service.ex.TableFieldContentDuplicateException;import org.springframework.data.domain.Page;import java.util.List;/** * 事件等级业务层 * @author SWJ */public interface EventLevelService {    /**     * 创建事件等级     * @param eventLevel     * @throws TableFieldContentDuplicateException     */    void createEventLevel(EventLevel eventLevel) throws TableFieldContentDuplicateException;    /**     * 分页查询     * @param pageNo     * @param pageSize     * @return     */    Page<EventLevel> findAllPage(int pageNo, int pageSize);    /**     * 查询全部     * @return     */    List<EventLevel> findAll();    /**     * 单个删除（或被迭代）     * @param eventLevelId     */    void delEventLevelById(String eventLevelId);    /**     * 多选删除     * @param ids     */    void deleteByEventLevelIdIn(List<String > ids);    /**     * 修改数据     * @param eventLevelId     * @param name     * @param eventLevelDescribe     */    void update(String eventLevelId,String name,String eventLevelDescribe);}