package com.ame.ser.controller;import com.ame.ser.service.ex.ParameterInvalidException;import com.ame.ser.model.EventLevel;import com.ame.ser.service.EventLevelService;import com.ame.ser.vo.ResultVO;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.data.domain.Page;import org.springframework.web.bind.annotation.*;import java.util.List;/** * 事件等级控制器 * @author SWJ */@RestController@RequestMapping("eventLevel")public class EventLevelController extends BaseController{    @Autowired    private EventLevelService eventLevelService;    @RequestMapping("create")    public ResultVO<Void> createEventLevel(@RequestBody EventLevel eventLevel){        if (eventLevel.getName()==null || eventLevel.getName().isEmpty()) {            throw new ParameterInvalidException("传入的参数是非法的，参考为:病区名字是空的");        }        eventLevelService.createEventLevel(eventLevel);        return new ResultVO<>(SUCCESS);    }    @RequestMapping("show")    public ResultVO<Page<EventLevel>> showAllEventLevelOnPage(int pageNo, int pageSize){        Page<EventLevel> PageEventLevel =  eventLevelService.findAllPage(pageNo, pageSize);        return new ResultVO<>(SUCCESS,PageEventLevel);    }    @RequestMapping("/del/{id}")    public ResultVO<Void> del(@PathVariable("id") String id ){        eventLevelService.delEventLevelById(id);        return new ResultVO<>(SUCCESS);    }    @RequestMapping("delAll")    public ResultVO<Void> deleteByEventLevelIdIn(String ids){        List<String> LString = regDelAllData(ids);        eventLevelService.deleteByEventLevelIdIn(LString);        return new ResultVO<>(SUCCESS);    }    @RequestMapping("update")    public ResultVO<Void> update(@RequestBody EventLevel eventLevel){        if (eventLevel.getName()==null) {            throw new ParameterInvalidException("传入的参数有误");        }        eventLevelService.update(eventLevel.getEventLevelId() , eventLevel.getName(), eventLevel.getEventLevelDescribe());        return new ResultVO<>(SUCCESS);    }}