package com.ame.ser.repository;import com.ame.ser.model.Ward;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.data.jpa.repository.Modifying;import org.springframework.data.jpa.repository.Query;import org.springframework.data.repository.query.Param;import org.springframework.transaction.annotation.Transactional;import java.util.List;/** * 病区持久层 * @author SWJ */public interface WardRepository extends JpaRepository<Ward,String>  {    Ward getByName(String name);    @Modifying    @Transactional    @Query(value="delete from Ward e where e.id in (:ids) ")    int deleteByWardIdIn(@Param("ids")List<String> ids);}